mapper.select:
	    select nvl(round(avg(rating),2), 0) as rating_avg, count(rating) as rating_count
        from star_rating
        where user_id = #{user_id}

select count(review_id)
from movie_review
where user_id = #{user_id}

select movie_id, movie_title, poster_path, rating
from
(
    select row_number() over(order by R.${order}) AS rno
         , M.movie_id, M.movie_title, M.poster_path
         , R.rating
    from movie M right join star_rating R
    on M.movie_id = R.movie_id
    where user_id = #{user_id}
    order by ${order}
) A
where rno between #{startRno} and #{endRno}

select A.review_id, A.movie_id, A.review_content, A.review_date, A.spoiler_status, A.number_of_likes, A.number_of_comments , A.user_id, A.profile_image, A.name , nvl(B.rating, 0) AS rating ,( select case Y.user_id when #{user_id} then 1 else 0 end as like_review from movie_review X join review_like Y on X.review_id = Y.review_id where Y.review_id = A.review_id ) AS like_review
from ( select row_number() over(order by R.review_date desc) AS rno , R.review_id, R.movie_id, R.review_content , to_char(R.review_date, 'yyyy-mm-dd') AS review_date , R.spoiler_status, R.number_of_likes, R.number_of_comments , M.user_id, M.profile_image, M.name from movie_review R join member M on R.user_id = M.user_id ) A join star_rating B on A.movie_id = B.movie_id where A.movie_id = #{movie_id}
and rno between #{startRno} and #{endRno}

select movie_id, movie_title, poster_path, backdrop_path, rating_count, rating_avg
from movie
where movie_id = #{movie_id}

select count(review_id)
from movie_review
where movie_id = #{movie_id}

select review_id, review_content, spoiler_status
from movie_review
where movie_id = #{movie_id} and user_id = #{user_id}

mapper.update:
update movie_review set review_content = #{review_content}
					  , spoiler_status = #{spoiler_status}
where review_id = #{review_id}

mapper.select:
select review_id, review_content, to_char(review_date, 'yyyy-mm-dd') AS review_date, spoiler_status, number_of_likes, number_of_comments
from movie_review
where movie_id = #{movie_id} and user_id = #{user_id}

mapper.update:
	update star_rating set rating = #{rating}
                         , rating_date = sysdate
    where movie_id = #{movie_id} and user_id = #{user_id}

mapper.select:
		select nvl(round(avg(rating),2), 0) as rating_avg, count(rating) as rating_count
        from star_rating
        where movie_id = #{movie_id}

mapper.update:
	update movie set rating_avg = #{rating_avg}
				   , rating_count = #{rating_count}
    where movie_id = #{movie_id}

update movie_diary set photo_front = #{photo_front}
					 , photo_back = #{photo_back}
where diary_id = #{diary_id}
