mapper.select:
SELECT m.movie_id, a.actor_id, COUNT(*) OVER (PARTITION BY a.actor_id) AS identical_actor_count, a.casting_order, b.actor_name FROM ( SELECT movie_id, user_id, rating, rating_date FROM star_rating
WHERE user_id = #{login_userid}
) w JOIN ( SELECT movie_id, movie_title, original_language, CONCAT('20', release_date) AS release_date, poster_path, rating_avg FROM Movie ) m ON w.movie_id = m.movie_id JOIN ( SELECT actor_id, movie_id, casting_order FROM movie_role ) a ON a.movie_id = m.movie_id JOIN ( SELECT actor_id, actor_name FROM actor ) b ON b.actor_id = a.actor_id ORDER BY identical_actor_count DESC, a.casting_order ASC FETCH FIRST ROW ONLY

select a.movie_id, a.movie_title, a.original_language, release_date, a.poster_path, a.rating_avg, COUNT(*) OVER (PARTITION BY b.Genre_id) AS count_Genre_id, c.Genre_id, c.Genre_name from ( SELECT movie_id, user_id, rating, rating_date FROM star_rating
WHERE user_id = #{login_userid}
) w JOIN( select movie_id, movie_title, original_language, CONCAT('20', release_date) AS release_date, poster_path, rating_avg from movie ) a ON w.movie_id = a.movie_id JOIN( select movie_id, Genre_id from movie_genre ) b ON a.movie_id = b.movie_id JOIN ( select Genre_id, Genre_name from genre ) c ON b.Genre_id = c.Genre_id ORDER BY count_Genre_id desc FETCH FIRST ROW ONLY


select  movie_id, movie_title, original_language, release_date, poster_path, rating_avg
from (
    select a.movie_id, a.movie_title, a.original_language, release_date, a.poster_path, a.rating_avg
    from (

        SELECT movie_id, movie_title, original_language, CONCAT('20', release_date) AS release_date, poster_path, rating_avg
        FROM Movie
    )a
    JOIN (
        SELECT movie_id, genre_id
        FROM movie_genre
        where genre_id = #{genre_id}
    )b
    ON a.movie_id = b.movie_id
    JOIN (
        SELECT genre_id, genre_name
        FROM genre
    ) c ON b.genre_id = c.genre_id
)
where ROWNUM <= 10
ORDER BY rating_avg desc


